def merge(key, l1, l2):
    k1 = { d[key] for d in l1 }
    k2 = { d[key] for d in l2 }
    keys = k1.intersection(k2)
    f1 = [ d for d in l1 if d[key] in keys ]
    f2 = [ d for d in l2 if d[key] in keys ]
    return f1, f2

