def print_latency(f):
    def wrapped(*args, **kwargs):
        try:
            start = time.time()
            r = f(*args, **kwargs)
        finally:
            print >>sys.stderr, 'Latency (%s): %.3fs' % (f.__name__, time.time() - start)
        return r
    wrapped.__name__ = f.__name__
    return wrapped

