    max_sum = 0
    start_of_max15 = 0
    for start in data_frame[column].values:
        series_sum = data_frame[column][data_frame[column].between(start, start + 15)].count()
        if series_sum > max_sum:
            max_sum = series_sum
            start_of_max15 = start
    return (start_of_max15, max_sum)

